DROP TABLE IF EXISTS admin
CREATE TABLE `admin` (  `Id` int(11) NOT NULL auto_increment,  `userid` varchar(40) default NULL,  `password` varchar(50) default NULL,  `lastlogin_time` int(11) default NULL,  PRIMARY KEY  (`Id`)) TYPE=MyISAM
DROP TABLE IF EXISTS img
CREATE TABLE `img` (  `Id` int(11) NOT NULL auto_increment,  `pid` int(11) default NULL,  `subject` varchar(100) default NULL,  `content` text,  `czyear` varchar(50) default NULL,  `types` int(11) default NULL,  PRIMARY KEY  (`Id`)) TYPE=MyISAM
DROP TABLE IF EXISTS infos
CREATE TABLE `infos` (  `Id` int(11) NOT NULL auto_increment,  `content` text,  `types` int(11) default NULL,  PRIMARY KEY  (`Id`)) TYPE=MyISAM
DROP TABLE IF EXISTS link
CREATE TABLE `link` (  `Id` int(11) NOT NULL auto_increment,  `pid` int(11) default NULL,  `linkname` varchar(100) default NULL,  `linkweb` varchar(100) default NULL,  `pic` varchar(50) default NULL,  `types` int(11) default NULL,  PRIMARY KEY  (`Id`)) TYPE=MyISAM
DROP TABLE IF EXISTS liuyan
CREATE TABLE `liuyan` (  `Id` int(11) NOT NULL auto_increment,  `name` varchar(30) default NULL,  `sex` varchar(4) default NULL,  `qq` varchar(20) default NULL,  `msn` varchar(50) default NULL,  `email` varchar(50) default NULL,  `homepage` varchar(50) default NULL,  `title` varchar(50) default NULL,  `content` mediumtext,  `instime` int(11) default NULL,  `isprivate` varchar(4) default NULL,  `ifpass` int(11) default NULL,  `read_num` int(11) default NULL,  `re_name` varchar(50) default NULL,  `re_content` mediumtext,  `re_id` int(11) default NULL,  `re_time` int(11) default NULL,  PRIMARY KEY  (`Id`)) TYPE=MyISAM
DROP TABLE IF EXISTS news
CREATE TABLE `news` (  `Id` int(11) NOT NULL auto_increment,  `pid` int(11) default NULL,  `subject` varchar(100) default NULL,  `pic` varchar(20) default NULL,  `content` mediumtext,  `instime` int(11) default NULL,  `type` char(2) default NULL,  `types` int(11) default NULL,  PRIMARY KEY  (`Id`)) TYPE=MyISAM
