INSERT INTO `n_article` (`a_id`, `sortnum`, `cid`, `a_state`, `a_title`, `a_title2`, `keywords`, `a_content`, `a_content2`, `a_url`, `a_pic`, `big_pic`, `flag`, `remen`, `sy`, `createtime`, `uptime`, `hitcount`) VALUES
(1, 1, 6, 1, '', '离婚了，公司财产如何分？', '', '<p>&nbsp;</p>\r\n<div align="left">&nbsp;</div>\r\n<div>&nbsp;</div>\r\n<div><span style="color: black; font-size: 9pt">1</span><span style="color: black; font-size: 9pt">、</span><span style="color: black; font-size: 9pt">夫妻关系存续期间，公司财产属于夫妻共同财产。</span></div>\r\n<div><span style="color: black; font-size: 9pt">&nbsp;&nbsp;&nbsp; </span><span style="color: black; font-size: 9pt">夫妻婚姻关系存续期间，因生产、经营收益属夫妻共同财产，在无特别约定情况下，夫妻离婚时对共同财产享有平分权利。</span></div>\r\n<div>&nbsp;</div>\r\n<div><span style="color: black; font-size: 9pt">2</span><span style="color: black; font-size: 9pt">、</span><span style="color: black; font-size: 9pt">股份比例不影响夫妻财产共同所有的关系。</span></div>\r\n<div><span style="color: black; font-size: 9pt">&nbsp;&nbsp;&nbsp; </span><span style="color: black; font-size: 9pt">工商登记中载明的夫妻投资比例并不能等同于夫妻之间财产约定，可以认定夫妻当初在工商登记的股份比例只是为设立公司形式上需要，非夫妻双方真实意思的表示，夫方主张完全由其经营、管理公司，按股份比例分割公司财产理由不充足。</span></div>\r\n<div>&nbsp;</div>\r\n<div><span style="color: black; font-size: 9pt">&nbsp;&nbsp;&nbsp; </span><span style="color: black; font-size: 9pt">公司经营所产生的股东收益均属于夫妻共同财产。</span></div>', '公司财产分割 \r\n', '', '', '', 1, 0, 0, '2012-06-06 09:35:49', '2012-06-07 09:45:42', 20),
(2, 2, 6, 1, '', '夫妻共同财产有哪些 ', '', '<p>夫妻共同财产有哪些 夫妻共同财产有哪些 夫妻共同财产有哪些 夫妻共同财产有哪些 夫妻共同财产有哪些 夫妻共同财产有哪些 夫妻共同财产有哪些</p>', '夫妻共同财产有哪些 \r\n夫妻共同财产有哪些 \r\n夫妻共同财产有哪些 \r\n夫妻共同财产有哪些 \r\n夫妻共同财产有哪些 \r\n', '', '', '', 1, 1, 0, '2012-06-06 09:36:08', '2012-06-06 14:03:15', 20),
(3, 3, 6, 1, '', '夫妻个人财产如何认定 ', '', '<p>夫妻个人财产如何认定 夫妻个人财产如何认定 夫妻个人财产如何认定 夫妻个人财产如何认定 夫妻个人财产如何认定 夫妻个人财产如何认定 夫妻个人财产如何认定</p>', '夫妻个人财产如何认定 \r\n', '', '', '', 1, 0, 1, '2012-06-06 09:36:20', '2012-06-06 14:05:21', 20),
(152, 12, 6, 1, '', '婚前股票婚后增值部分属于夫妻共同财产', '婚前财产', '<p>&nbsp;</p>\r\n<div align="left">&nbsp;</div>\r\n<div><span style="color: black; font-size: 13.5pt">&nbsp;&nbsp;&nbsp; 李某（男）与黄某（女）恋爱二年后结婚，婚前李某的父母给李某10万元，李某购买了股票。婚后，李某利用业余时间炒股，有时也利用家中部分积蓄购买了股票，但在赢利后及时将挪用的家中积蓄退出股市。股票一直由李某操作卖买，近期股票已增值至33万元。现李某与黄某因生活琐事发生<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">矛盾</font></span></span>，夫妻感情急剧恶化，黄某向法院提起<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">离婚诉讼，</font></span></span>并要求分割股票的增值部分。</a></a></span></div>\r\n<div><span style="color: black; font-size: 13.5pt"><br />\r\n</span>&nbsp;</div>\r\n<div><span style="color: black; font-size: 13.5pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span><span style="color: black; font-size: 13.5pt">本案在审理中，产生了二种分岐意见。第一种意见认为，股票增值部分是依附于<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">股票</font></span></span>本金而产生，无股票本金也就无增值部分可言，由于股票本金属于李某的婚前财产，股票的增值部分也属于李某的婚前财产，黄某无权享受该笔财产。第二种意见认为，炒股有亏有赢，李某也有用家庭积蓄参与炒股的情况，虽然李某在赢利后将家庭积蓄退出股市，但所得的赢利已无法分清是何部分股金所产生，且该部分收入是在夫妻关系<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">存续期间</font></span></span>所得，应认定为<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">共同财产</font></span></span>，黄某可以分享股票<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">增值部分</font></span></span>的财产。最后，法院作出了10万元股金仍属李某的婚前财产，其余<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">增值部分</font></span></span>由双方各半分享的判决。</a></a></a></a></a></span></div>\r\n<div><span style="color: black; font-size: 10pt"><br />\r\n&nbsp;</span></div>\r\n<div><span style="color: black; font-size: 13.5pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="color: black; font-size: 13.5pt">李某婚前购买的10万元股票，是其婚前投入的个人财产，剩下的23万元既可能是婚前所购股票的增值，也有可能是婚后投入的财产的增值，炒股期间亏损部分应算作婚前个人财产的亏损还是婚后共同财产部分的<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">亏损</font></span></span>，该10万元中曾亏损了多少，已无法区分。<br />\r\n&nbsp;<br />\r\n</a></span><span style="color: black; font-size: 13.5pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="color: black; font-size: 13.5pt">根据《婚姻法》第十七条规定，夫妻在婚姻关系存续期间所得的生产、<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">经营收益</font></span></span>，归夫妻共同所有。该规定明确了认定夫妻共同财产的范围。股票的<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">增值</font></span></span>部分作为一种生产、经营收入，只要是在婚姻关系存续期间取得的，就应当属于<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">夫妻共同财产</font></span></span>。从<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">举证责任</font></span></span>分配原则来看，最高人民法院《关于人民法院审理离婚案件处理财产分割问题的若干具体意见》第七条规定，对个人财产还是夫妻共同财产难以认定的，主张权利的一方负<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">举证责任</font></span></span>。当事人举不出有力<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">证据</font></span></span>的，人民法院又无法查实的，按夫妻共同财产处理。本案中李某认为股票增值部分财产主要是自己<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">婚前财产</font></span></span>的增值，但其举不出证据证实自己的主张，故这笔增值财产应作为夫妻<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">共同财产</font></span></span>处理。<br />\r\n<br />\r\n</a></a></a></a></a></a></a></a></span><span style="color: black; font-size: 13.5pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span><span style="color: black; font-size: 13.5pt">李某婚后业余时间从事炒股，黄某对股票增值部分其实也作出了贡献。不能认为黄某没有参与炒股，就对股票增值部分没有付出。李某有时以自己的工资收入注入股市，为股票的增值提供了资金支持。另一方面，黄某在家庭生活中承担了较多的家庭义务，为李某炒股提供了时间上的支持及生活上的方便。所以，黄某对股票增值也付出了劳动。<br />\r\n<br />\r\n</span><span style="color: black; font-size: 13.5pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="color: black; font-size: 13.5pt">众所周知，股市有风险。本案中股票的增值可能发生在李某婚前，也可能在婚后，还有可能在某一时段发生<a target="_blank" href="http://www.bjlawyer.bj.cn/"><span><span><font color="#800080">严重亏损</font></span></span>，后又大幅增值。法院从该笔股金中划出10万元作为李某的个人财产，余款作为共同财产由双方分享，已经综合考虑了程序和实体方面的规定及日常生活经验，体现了公平、公正.</a></span></div>', '', '', '', '', 1, 0, 0, '2012-06-07 09:15:16', '2012-06-07 09:15:16', 5),
(6, 6, 6, 1, '', '离婚聘请北京婚姻律师能帮你做什么 ', '', '离婚聘请北京婚姻律师能帮你做什么 \r\n离婚聘请北京婚姻律师能帮你做什么 \r\n离婚聘请北京婚姻律师能帮你做什么 \r\n离婚聘请北京婚姻律师能帮你做什么 \r\n离婚聘请北京婚姻律师能帮你做什么 \r\n离婚聘请北京婚姻律师能帮你做什么 \r\n', '离婚聘请北京婚姻律师能帮你做什么 \r\n', '', '', '', 1, 0, 0, '2012-06-06 09:36:58', '2012-06-06 09:36:58', 20),
(9, 9, 6, 1, '', '离婚时怎么分割经济适用房 ', '', '离婚时怎么分割经济适用房 \r\n离婚时怎么分割经济适用房 \r\n离婚时怎么分割经济适用房 \r\n离婚时怎么分割经济适用房 \r\n离婚时怎么分割经济适用房 \r\n离婚时怎么分割经济适用房 \r\n离婚时怎么分割经济适用房 \r\n', '离婚时怎么分割经济适用房 \r\n', '', '', '', 1, 0, 0, '2012-06-06 09:37:32', '2012-06-06 09:37:32', 20),
(158, 15, 6, 1, '', '个人财产', '', '<p><font face="宋体"><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana">夫妻一方个人财产包括：</span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&nbsp;<o:p></o:p></span></p>\r\n<p><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(1)</span><font face="宋体"><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana">夫妻一方婚前财产，包括婚前的动产与不动产，主要的婚前财产有：股票、债券、个人的劳动所得、继承或受赠的财产、婚前各自为结婚所购置的财产（如房产）等等。属于一方的婚前个人财产在没有明确的书面约定的情况下，在离婚时视为一方的个人财产，不作为共有财产分割。</span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&nbsp;<o:p></o:p></span></p>\r\n<p><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana"><font face="宋体">　　</font></span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">(2)</span><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana"><font face="宋体">复员、转业军人从部队带回来的医疗费和回乡生产补助费，以及婚后夫妻共同生活时间较短的军人的复员费和转业费</font></span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">;<o:p></o:p></span></p>\r\n<p><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&nbsp;<o:p></o:p></span></p>\r\n<p><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&nbsp;&nbsp;&nbsp;&nbsp; (3)</span><font face="宋体"><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana">一方专用的生活用品。</span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&nbsp;<o:p></o:p></span></p>\r\n<p><font face="宋体"><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana">&nbsp;&nbsp;&nbsp; 这里需要强调的是：一是生活用品，二是必须为夫妻一方专用，如书籍、衣服、生活用品、职业上的用品。在司法实践中，如果是用夫妻共有财产购买的价值较大的生活用品，或者在夫妻共有财产中所占比重较大的用品，在判决归属一方的同时，对另一方应予于适当的照顾。</span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&nbsp;<o:p></o:p></span></p>\r\n<p><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (4)</span><font face="宋体"><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana">离婚时与个人身份密切相关的婚后所得财产，和未获得经济效益的知识产权</span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&nbsp;<o:p></o:p></span></p>\r\n<p><font face="宋体"><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana">&nbsp;&nbsp;&nbsp;&nbsp;夫妻一方因身体受到伤害获得的医疗费、残疾人生活补助费，因这些财产与人的生命、健康等人身属性相关，应视为夫妻个人所有的财产，在离婚时不作为夫妻共有财产分割。需要注意的是：在离婚时，如果因夫妻一方受到伤害，另一方或者家属获得的被抚养人生活费，因赔偿对象并非受害者本人，故此费应归被抚养人所有，而不能计入一方个人财产。</span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&nbsp;<o:p></o:p></span></p>\r\n<p><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (5)</span><font face="宋体"><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana">双方约定为夫妻一方个人财产的</span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt"><o:p></o:p></span></font></p>\r\n<p><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&nbsp;<o:p></o:p></span></p>\r\n<p><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana"><font face="宋体">&nbsp;&nbsp;&nbsp;《中华人民共和国婚姻法》规定：夫妻对婚姻关系存续期间所得的财产以及婚前财产的约定，对双方具有约束力。第十九条第一款规定</font></span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&ldquo;</span><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana"><font face="宋体">约定应当采用书面形式。没有约定或约定不明确的，适用该法第</font></span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">17</span><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana"><font face="宋体">条、第</font></span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">18</span><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana"><font face="宋体">条的规定。</font></span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt">&rdquo;</span><span style="color: black; font-size: 9pt; mso-ascii-font-family: Verdana; mso-hansi-font-family: Verdana"><font face="宋体">由此可见，我国夫妻财产约定制应当采取书面形式，而不能采用口头形式及其他形式。</font></span><span lang="EN-US" style="font-family: Verdana; color: black; font-size: 9pt"><o:p></o:p></span></p>\r\n<p>&nbsp;</p>', '', '', '', '', 1, 0, 0, '2012-06-07 09:53:27', '2012-06-07 09:53:27', 5),
(11, 11, 6, 1, '', '离婚后因房屋主贷人变更引起纠纷怎么办？ ', '', '离婚后因房屋主贷人变更引起纠纷怎么办？ \r\n离婚后因房屋主贷人变更引起纠纷怎么办？ \r\n离婚后因房屋主贷人变更引起纠纷怎么办？ \r\n离婚后因房屋主贷人变更引起纠纷怎么办？ \r\n离婚后因房屋主贷人变更引起纠纷怎么办？ \r\n离婚后因房屋主贷人变更引起纠纷怎么办？ \r\n离婚后因房屋主贷人变更引起纠纷怎么办？ \r\n', '离婚后因房屋主贷人变更引起纠纷怎么办？ \r\n', '', '', '', 1, 0, 0, '2012-06-06 09:37:58', '2012-06-06 09:37:58', 21),
